import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

class OneArticle extends Component {

  constructor(props) {
    super(props);
    this.state = {
      article: {}
    };
  }

  componentDidMount() {
    axios.get('/article/'+this.props.match.params.id)
      .then(res => {
        this.setState({ article: res.data });
        console.log(this.state.article);
      });
  }

  delete(id){
    console.log(id);
    axios.delete('/article/'+id)
      .then((result) => {
        this.props.history.push("/profile")
      });
  }

  render() {
    return (
      <div class="container">
        <div class="panel panel-default">
          <div class="panel-heading">
            <h3 class="panel-title">
              {this.state.article.title}
            </h3>
          </div>
          <div class="panel-body">
            <h4><Link to="/"><span class="glyphicon glyphicon-th-list" aria-hidden="true"></span> Blog List</Link></h4>
            <dl>
              <dt>Title:</dt>
              <dd>{this.state.article.isbn}</dd>
              <dt>Author:</dt>
              <dd>{this.state.article.author}</dd>
              <dt>Description:</dt>
              <dd>{this.state.article.description}</dd>
              <dt>Publish Date:</dt>
              <dd>{this.state.book.createdAt}</dd>
            </dl>
            <Link to={`/edit/${this.state.book._id}`} class="btn btn-success">Edit</Link>&nbsp;
            <button onClick={this.delete.bind(this, this.state.book._id)} class="btn btn-danger">Delete</button>
          </div>
        </div>
      </div>
    );
  }
}

export default OneArticle;
