import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Switch, Redirect} from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import Landing from './components/Landing';
import Profile from './components/Profile';
import Article from './components/Article';
import OneArticle from './components/OneArticle';
import ShowArticles from './components/ShowArticles';
import EditArticle from './components/EditArticle';
import PrivateRoute from "./components/PrivateRoute"
// import {Whoops404} from './components/Whoops404';
// import 'bootstrap/dist/css/bootstrap.css';

class App extends Component {
    state = {
        loggedIn: false,
        user: {}
      }

      loggedIn = (loginState) => {
        const {loggedIn, user} = loginState
        this.setState({loggedIn: loggedIn, user: user})
        localStorage.setItem("usertoken", JSON.stringify(loginState))
      }

      componentDidMount() {
        this.setState(JSON.parse(localStorage.getItem("usertoken")))
      }

    render() {
        return (

            <Router>
                <div className="App">
                    <Navbar/>
                    <Route exact path="/" component={Landing}/>
                    <div className="container">
                    <Switch>
                    <Route path="/login" render={(props)=> <Login {...props} loggedIn={this.loggedIn}/>} />
          <Route path="/register" render={(props)=> <Register {...props} loggedIn={this.loggedIn}/>} />
                        {/* <Route exact path="/register" component={Register}/>
                        <Route path="/login" component={Login}/> */}
                        <Route path="/profile" component={Profile}/>
                        <Route path="/article" component={Article}/>   {/*Add articles form */}
                        <Route path="/articles" component={ShowArticles}/>
                        <Route path='/show/:id' component={OneArticle} />  {/*Show a single article */}
                        <Route path='/edit/:id' component={EditArticle} />
                        {/* <Route component={Whoops404} /> */}
                        {/* Maybe you need to check if user is logged in before using Redict router */}
                        {/* <Redirect from='/profile' to='/login'/>
                        <Redirect from='/article' to='/login'/>
                        <Redirect from='/articles' to='/login'/> */}

                    </Switch>
                    </div>
                </div>
            </Router>
        );
    }
}

export default App;
