import axios from 'axios';


class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {}
    };
  }

  componentDidMount() {
		axios.get('/users/one/'+this.props.match.params.id)
      .then(res => {
				debugger
				this.setState({ user: res.data.user });
