import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

class EditArticle extends Component {

  constructor(props) {
    super(props);
    this.state = {
      article: {}
    };
  }

  componentDidMount() {
    axios.get('/articles/'+this.props.match.params.id)
      .then(res => {
        this.setState({ article: res.data.article });
        console.log(this.state.article);
      });
  }

  onChange = (e) => {
    const state = this.state.article
    state[e.target.name] = e.target.value;
    this.setState({article:state});
  }

  onSubmit = (e) => {
    e.preventDefault();

    const { title, body, author } = this.state.article;

    axios.put('/articles/'+this.props.match.params.id, { title, body, author })
      .then((result) => {
        this.props.history.push("/show/"+this.props.match.params.id)
      });
  }

  render() {
    return (
      <div class="container">
        <div class="panel panel-default">
          <div class="panel-heading">
            <h3 class="panel-title">
              EDIT BLOG
            </h3>
          </div>
          <div class="panel-body">
            <h4><Link to={`/show/${this.state.article._id}`}><span class="glyphicon glyphicon-eye-open" aria-hidden="true"></span> Blog List</Link></h4>
            <form noValidate onSubmit={this.onSubmit}>
                          <h1 className="h3 mb-3 font-weight-normal">Add Article</h1>
                          <div className="form-group">
                              <label htmlFor="title">
                                  Title:
                              </label>
                              <input
                                  type="text"
                                  className="form-control"
                                  name="title"
                                  placeholder="Title"
                                  value={this.state.title}
                                  onChange={this.onChange}/>
                          </div>
                          <div className="form-group">
                              <label htmlFor="author">
                                  Author:
                              </label>
                              <input
                                  type="text"
                                  className="form-control"
                                  name="author"
                                  placeholder="Author"
                                  value={this.state.author}
                                  onChange={this.onChange}/>
                          </div>
                          <div className="form-group">
                              <label htmlFor="body">
                                  Description
                              </label>
                              <textarea
                                  type="text"
                                  className="form-control"
                                  name="body"
                                  placeholder="Enter Post"
                                  value={this.state.body}
                                  onChange={this.onChange}/>
                          </div>
                          <button type="submit" className="btn btn-lg btn-primary btn-block">
                              Post
                          </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default EditArticle;
