import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

class OneArticle extends Component {

  constructor(props) {
    super(props);
    this.state = {
    oneArticle: []

    }
  }

  componentDidMount() {
    axios.get('/articles/'+this.props.match.params.id)
      .then(res => {
        this.setState({ oneArticle: res.data });
        console.log(this.state.oneArticle.article.title);
      });
  }

  delete(id){
    console.log(id);
    axios.delete('/articles/'+id)
      .then((result) => {
        this.props.history.push("/profile")
      });
  }

  render() {
    return (
      <div className="container">
        <div className="panel panel-default">
          <div className="panel-heading">
          </div>

          {this.state.OneArticle.map(article =>
                  <tr>
                    <td><Link to={`/show/${article._id}`}>{article.title}</Link></td>
                    <td>{article.body}</td>
                    <td>{article.author}</td>
                  </tr>
                )}

          {/* <div className="panel-body">
            <h4><Link to="/"><span className="glyphicon glyphicon-th-list" aria-hidden="true"></span> Blog List</Link></h4>
