import React, {Component} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';



class ShowArticles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }

  componentDidMount() {
    axios.get('/articles')
      .then(res => {
        this.setState({ articles: res.data });
        console.log(this.state.articles);
      });
  }

  render() {
    return (
      <div className="container">
      <div className="row">
          <div className="col-md-1"></div>
        <div className="col-md-6">
          <div id="postlist">
            <div className="panel">
                      <div className="panel-heading">
                          <div className="text-center">
                              <div className="row">
                                  <div className="col-sm-9">
                                      <h3 className="pull-left">Welcome</h3>
                                  </div>
                              </div>
                          </div>
                      </div>                   
                  <div className="panel-body">
                  {this.state.articles.map(article =>

           <p><Link to={`/show/${article._id}`}>{article.title}  Read MORE....</Link></p>

                      )} 
                  </div>

                  <div className="panel-footer">
                      <span className="label label-default">Welcome</span> <span className="label label-default">Updates</span> <span className="label label-default">July</span>
                  </div>
              </div>

              </div>
          </div>
          <div className="text-center"><a href="#" id="loadmore" className="btn btn-primary">Older Posts...</a></div>
      </div>
        <div className="col-md-1"></div>
        <div className="col-md-3">
        </div>
        <div className="col-md-1">
        </div>
      </div>

    );
  }
}

export default ShowArticles;
