import React, { Component } from 'react';
import axios from 'axios';
// import { Link } from 'react-router-dom';
// import {handleUpload} from './UserFunctions';

class EditProfile extends Component {

  constructor(props) {
      super(props)
      this.state = props.user
  }
  state = {
      first_name: "",
      last_name: "",
      email: "",
      password: ""
  }

  handleChange = (event) => {
      const {name, value} = event.target
      this.setState({[name]: value})
  }

  handleSubmit = (event)=> {
      event.preventDefault()
      let newUser = this.state
      axios({
              method: "post",
              url: `${'localhost:3001'}/users`, 
              data: newUser,
              withCredentials: true,
          })
          .then((response)=> {
              debugger
              console.log("Success")
              this.props.loggedIn(true)
          })
          .catch((err)=> {
              console.log("Error error")
          })
  }
render() {
  return (
    <div>
      <form onSubmit={this.handleSubmit}>
             <div className="field">
                  <div className="label">First name</div>
                  <div className="control">
                      <input className="input" onChange={this.handleChange} type="text" name="first_name" placeholder="first_name" value={this.state.first_name}/>
                  </div>
             </div>
             <div className="field">
                 <div className="label">Last name</div>
                  <div className="control">
                      <input className="input" onChange={this.handleChange} type="text" name="last_name" placeholder="last_name" value={this.state.last_name} />
                  </div>
             </div>
             <div className="field">
                 <div className="label">Email</div>
                  <div className="control">
                      <input className="input" onChange={this.handleChange} type="text" name="email" placeholder="email" value={this.state.email}/>
                  </div>
             </div>
             <div className="field">
                 <div className="label">Password</div>
                  <div className="control">
                      <input className="input" placeholder="password" onChange={this.handleChange} name="password" type="password" value={this.state.password}/>
                  </div>
             </div>
             <div className="field">
                  <div className="control">
                      <input className="button" type="submit" value="Submit" />
                  </div>
              </div>
          </form>
    </div>
  )
}
}


export default EditProfile;
