import React, {Component} from 'react';
import {login as login} from './UserFunctions';
import '../components/css/login.css';
import { Message } from 'semantic-ui-react';

const initialState = {
    email: '',
    password: '',
    emailError: '',
    passwordError: '',
    error: false,
}

class Login extends Component {
    constructor(props) {
        super(props)

        this.state = initialState;

        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }

    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    validate = () => {

       let  emailError =  '';
       let  passwordError = '';

       if (!this.state.email.includes('@')) {
           emailError = 'invalid email';
       }

       if (!this.state.password) {
        passwordError = 'password cannot be blank';
    }


       if (emailError || passwordError) {
           this.setState({ emailError, passwordError });
           return;
       }
       return true;
    }

    onSubmit(e) {
        e.preventDefault();

        const isValid = this.validate();
        if (isValid) {
            console.log(this.state); 
            //clear form
           this.setState({initialState});
        } else {
            const user = {
                email: this.state.email,
                password: this.state.password
            }

            login(user).then(res => {

                this.props.history.push('/')  
       })
        }


    }



    render() {
        const { error } = this.state;
        return (
            <div className="center">
                <div className="carding">
                    <div className="col-md-10">
                        <form noValidate error={error} onSubmit={this.onSubmit}>
                            <h1 className="h3 mb-3 font-weight-normal">Please Log In</h1>
                            {error && <Message error={error} 
                             content="That username/password is incorrect. Try again!"  />}
                            <div className="form-group">
                                <label htmlFor="email">
                                    Email Address
                                </label><br />
                                <input
                                    type="email"
                                    className="form-item"
                                    name="email"
                                    placeholder="Enter email"
                                    value={this.state.email}
                                    onChange={this.onChange}/>
                            </div> 
                            <div style={{ fontSize: 14, color: "red"}}>{this.state.emailError}</div><br />
                            <div className="form-group">
                                <label htmlFor="email">
                                    Password
                                </label> <br />
                                <input
                                    type="password"
                                    className="form-item"
                                    name="password"
                                    placeholder="Enter password"
                                    value={this.state.password}
                                    onChange={this.onChange}/>
                            </div>
                            <div style={{ fontSize: 14, color: "red"}}>{this.state.passwordError}</div>
                            <button type="submit" className="btn btn-lg btn-primary btn-block">
                                Sign in
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default Login;
